mapper.select:
SELECT


,equipment.equipment_uuid_number as equipmentUuidNumber
,equipment.equipment_name as equipmentName
,equipment.equipment_photo as equipmentPhoto
,equipment.equipment_types as equipmentTypes
,equipment.equipment_kucun_number as equipmentKucunNumber
,equipment.equipment_new_money as equipmentNewMoney
,equipment.equipment_clicknum as equipmentClicknum
,equipment.equipment_content as equipmentContent
,equipment.shangxia_types as shangxiaTypes
,equipment.equipment_delete as equipmentDelete
,yonghu.yonghu_name as yonghuName
,yonghu.yonghu_phone as yonghuPhone
,yonghu.yonghu_id_number as yonghuIdNumber
,yonghu.yonghu_photo as yonghuPhoto
,yonghu.yonghu_email as yonghuEmail
,yonghu.new_money as newMoney

FROM equipment_commentback  a
left JOIN equipment equipment ON a.equipment_id = equipment.id
left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



        and a.id in

        #{item}



        and (
            a.equipment_id = #{params.equipmentId}
        )


        and (
            a.yonghu_id = #{params.yonghuId}
        )


        and a.equipment_commentback_text like CONCAT('%',#{params.equipmentCommentbackText},'%')


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.reply_text like CONCAT('%',#{params.replyText},'%')


          and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart}) 


          and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd}) 




        and a.equipment_id IS NOT NULL


        and equipment.equipment_uuid_number like CONCAT('%',#{params.equipmentUuidNumber},'%')


        and equipment.equipment_name like CONCAT('%',#{params.equipmentName},'%')


        and equipment.equipment_types = #{params.equipmentTypes}



          and equipment.equipment_kucun_number >= #{params.equipmentKucunNumberStart}   


          and equipment.equipment_kucun_number <= #{params.equipmentKucunNumberEnd}   


        and equipment.equipment_kucun_number = #{params.equipmentKucunNumber}


          and equipment.equipment_new_money >= #{params.equipmentNewMoneyStart}   


          and equipment.equipment_new_money <= #{params.equipmentNewMoneyEnd}   


          and equipment.equipment_clicknum >= #{params.equipmentClicknumStart}   


          and equipment.equipment_clicknum <= #{params.equipmentClicknumEnd}   


        and equipment.equipment_clicknum = #{params.equipmentClicknum}


        and equipment.equipment_content like CONCAT('%',#{params.equipmentContent},'%')


        and equipment.shangxia_types = #{params.shangxiaTypes}



          and equipment.equipment_delete >= #{params.equipmentDeleteStart}   


          and equipment.equipment_delete <= #{params.equipmentDeleteEnd}   


        and equipment.equipment_delete = #{params.equipmentDelete}


          and UNIX_TIMESTAMP(equipment.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(equipment.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 



        and a.yonghu_id IS NOT NULL


        and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


        and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


        and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


        and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


          and yonghu.new_money >= #{params.newMoneyStart}   


          and yonghu.new_money <= #{params.newMoneyEnd}   


order by a.${params.sort} ${params.order}
