mapper.select:
SELECT



FROM equipment  a



        and a.id in

        #{item}



        and a.equipment_uuid_number like CONCAT('%',#{params.equipmentUuidNumber},'%')


        and a.equipment_name like CONCAT('%',#{params.equipmentName},'%')


        and a.equipment_types = #{params.equipmentTypes}


          and a.equipment_kucun_number >= #{params.equipmentKucunNumberStart}   


          and a.equipment_kucun_number <= #{params.equipmentKucunNumberEnd}   


        and a.equipment_kucun_number = #{params.equipmentKucunNumber}


          and a.equipment_new_money >= #{params.equipmentNewMoneyStart}   


          and a.equipment_new_money <= #{params.equipmentNewMoneyEnd}   


          and a.equipment_clicknum >= #{params.equipmentClicknumStart}   


          and a.equipment_clicknum <= #{params.equipmentClicknumEnd}   


        and a.equipment_clicknum = #{params.equipmentClicknum}


        and a.equipment_content like CONCAT('%',#{params.equipmentContent},'%')


        and a.shangxia_types = #{params.shangxiaTypes}


          and a.equipment_delete >= #{params.equipmentDeleteStart}   


          and a.equipment_delete <= #{params.equipmentDeleteEnd}   


        and a.equipment_delete = #{params.equipmentDelete}


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 



order by a.${params.sort} ${params.order}
